from scapy.all import *

# hex stream 문자열
hex_streams = ["00807f2851dc00807f2851d908004500005415e400004011e341c0a8001bc0a8000804091ecb0040ece35254505301020101c0a8001bfcd6e001090000080000025506a9000003000018000000000000010300000000000000220005000814250186",
              "00807f2851d6909f33eb4aa6080045000060d90900008011e026c0a80002c0a8000a0a671d8b004c682c5254505301020101c0a801c9000b0000090108004a1def67002d0c2203012400000000000000b703000000000f00000010d0140017050501000000010000010101010000",
              "",
              ]

# hex 문자열을 bytes로 변환
raw_bytes = [bytes.fromhex(hex_stream) for hex_stream in hex_streams]

# Scapy의 Raw()를 이용해 패킷 생성
packets = [Ether(raw_byte) for raw_byte in raw_bytes]

# 전송 (인터페이스는 상황에 맞게 설정)
for packet in packets:
    sendp(packet, iface="이더넷")